(function ($, undefined) {

  var $menuToggle = $('#main-nav__toggle-js'),
      $mainNavList = $('#main-nav__list'),
      $mainNavLink = $('.main-nav__link'),
      $mainNav = $('#main-nav'),
      $carousel = $('.owl-carousel'),
      $body = $('body')

  $menuToggle.click(function () {
    $menuToggle.toggleClass('main-nav__toggle--active');
    $mainNavList.toggleClass('visuallyhidden main-nav__list--active');
    $mainNav.toggleClass('main-nav--active');
    $body.toggleClass('no-scroll');
  });

  $mainNavLink.click(function () {
    $menuToggle.toggleClass('main-nav__toggle--active');
    $mainNavList.toggleClass('visuallyhidden main-nav__list--active');
    $mainNav.toggleClass('main-nav--active');
    $body.toggleClass('no-scroll');
  });

  $carousel.owlCarousel({
    loop: true,
    dots: true,
    items: 3,
    margin: 30,
    // autoplay: true,
    // autoplayHoverPause: true,
    // autoplayTimeout: 7000,
    responsive : {
      0 : {
          items : 1,
      },
      768 : {
          items : 2,
      },
      1170 : {
          items : 3,
      }
    }
  });

  $("a[href*='#']").mPageScroll2id();

})(jQuery);
